add_action( 'login_enqueue_scripts', function () {

    print '<style type="text/css">
.keepass-button {
    text-align:center;
}
.keepass-button a {
    display:inline-block;
    width:48px;
    height:48px;
    margin:-2px 0 16px 0;
}
.keepass-button a:focus,
.keepass-button a:hover {
    border-radius: 50%;
    box-shadow: 0 0 3px #004057;
}
</style>' . "\n";

} );

add_action( 'login_form', function () {

    print '<p class="keepass-button"><a tabindex="10" title="Install KeePass" target="_blank" href="http://keepass.info/download.html"><img width="48" height="48" alt="KeePass"
src="data:image/png;base64,"
/></a></p>' . "\n";
} );
